    /* 画弧线*/
    UIBezierPath *path  = [UIBezierPath bezierPathWithArcCenter:CGPointMake(70 , 70) radius:70 startAngle:M_PI/4 endAngle:M_PI/2 clockwise:YES];
    [[UIColor redColor]set];
