    //45度 90度 45度 90 度
    NSArray *array = @[@"25",@"30",@"25",@"20"];
    CGFloat startAgle = 0;
    CGFloat endAgle   = 0;

    for(NSString *currAgle in array)
    {
        //将百分比  转换为 弧度
        CGFloat currFloatAgle = currAgle.integerValue / 100.0 * M_PI*2;
