        //每次重新创建一个  path
        UIBezierPath *path = [UIBezierPath bezierPath];
        [path addArcWithCenter:CGPointMake(70, 70) radius:50 startAngle:startAgle endAngle:endAgle clockwise:YES];
        [path addLineToPoint:CGPointMake(70, 70)];
        //每次随机生成一个颜色
        [[self changeColor]set];

        [path fill];

    }
}
