    UIBezierPath *path =[UIBezierPath bezierPathWithArcCenter:CGPointMake(50, 50) radius:30 startAngle:M_PI_4 endAngle:M_PI clockwise:YES];
    // 2. 添加线 到 圆心
    [path addLineToPoint:CGPointMake(50, 50)];
