    UIImage *newImg = UIGraphicsGetImageFromCurrentImageContext();

    /**
     *  关闭上下文
     */
    UIGraphicsEndImageContext();

    /**
     *  压缩写入文件
     */
    NSData *jpegData = UIImageJPEGRepresentation(newImg, 1.);
    [jpegData writeToFile:@"/Users/mac/Desktop/text.jpg" atomically:YES];

